import React, { useState, useEffect } from 'react';
import "./Vote.css";
import Schedule from "./Schedule/Schedule";

function Vote() {

    const [votes, setVotes] = useState([]);

    const getThings = async () => {
		const response = await fetch('/index', { method: 'POST'});
		const jsonResponse = await response.json();
        setVotes(jsonResponse[1].votes);
        console.log(jsonResponse[1].votes)
	}

    useEffect(() => {
        getThings();
    }, [])


    return (
        <div className="vote">
            <div>
                <h1>Horaires</h1>
                <small>* plusieurs choix sont possibles</small>
            </div>
            {
                votes.length && votes.map((votes, index) => <Schedule votes={votes} schedule={index} />)
            }
            <button className="vote-btn">Vote</button>
        </div>
    )
}

export default Vote;
