import React, {useState} from 'react'

const Payment = () => {

    new Cleave('.input-0', {
        creditCard: true,
        onCreditCardTypeChanged: function (type) {
            document.querySelector('.type').innerHTML = type;
        }
    });

    return (
        <div>
            <h1>réservation</h1>
            <h4>Si la demande pour la ligne n'atteint pas le minimum requis vous ne serez pas débité</h4>
            <input class="input-element" type="text"/>
            <link to="PayOk"><input placeholder="Adresse mail" required="required"></input> <h4>Restez au courant de l'avancé de votre réservation et recevez votre Qr Code par mail</h4></link>
            <button>Réserver</button>
        </div>
    )
}

export default Payment
