import React from 'react'
import "./Schedule.css";

function Schedule({ schedule, amount }) {

    const [choosen, setChoosen] = useState();

    const chooseSchedule = event => {
        const schedule = event.currentTarget;
        schedule.className === "schedule" && schedule.classList.toggle('choosen')
    }

    return (
        <div className="schedule" onClick={chooseSchedule}>
            <div className="info">
                <div className="schedule-time">Bus pour {schedule}h</div>
                <div className="percentage">{amount}%</div>
            </div>
            <div className="bar-container">
                <div className="bar" style={{ width: `${amount}%` }}>
                    <div className="bar-lightning" style={{animation: 'choosen infinite 2.5s'}}></div>
                </div>
            </div>
        </div>
    )
}

export default Schedule;
