import { Link } from 'react-router-dom'
import Cleave from 'cleave.js/dist/cleave-react';
import './payment.css'

const Payment = () => {

    function onCreditCardChange (event) {
        // formatted pretty value
        console.log(event.target.value);

        // raw value
        console.log(event.target.rawValue);
    }

    return (
        <div className="contentFirst">
            <h1 className="rese">réservation</h1>
            <h4>Si la demande pour la ligne n'atteint pas le minimum requis vous ne serez pas débité</h4>
            <section>
            <Cleave placeholder="Enter your credit card number"
                options={{creditCard: true}}
                onChange={onCreditCardChange} />
            </section>
            <input placeholder="Adresse mail" required="required"></input> <h4>Restez au courant de l'avancé de votre réservation et recevez votre Qr Code par mail</h4>
            <Link to="PayOk"><button>Réserver</button></Link>
        </div>
    )
}

export default Payment
