    const [jour, setJour] = useState(21)
    const [jour2, setJour2] = useState(jour+1)
    const [jour3, setJour3] = useState(jour+1)
    const [jour4, setJour4] = useState(jour+1)
    const [jour5, setJour5] = useState(jour+1)
    const [jour6, setJour6] = useState(jour+1)
    const [jour7, setJour7] = useState(jour+1)
    const [mois, setMois] = useState(12)
    const [year, setYear] = useState(2019)

    const recupId = (event) => {
        setJourSelect(event.target.id)
    }

    const takeId = (jourSelect) => {
        setDayChoose(jourSelect)
      }

    return (
        <div className="contentDate">
            <div className="titleJour">
                <h4 className="textJour">Date de retour nocturne</h4>
            </div>
                <div className="tabDate">
                    <button onClick={recupId} id="1" className="weekDate"><p className="headDate">Samedi</p><p className="date">21/{mois}/{year}</p></button>
                    <button onClick={recupId} id="2"className="weekDate"><p className="headDate">Dimanche</p><p className="date">{jour2}/{mois}/{year}</p></button>
                    <button onClick={recupId} id="3"className="semaineDate"><p className="headDate">lundi</p><p className="date">23/12/2019</p></button>
                    <button onClick={recupId} id="4"className="semaineDate"><p className="headDate">mardi</p><p className="date">24/12/2019</p></button>
                    <button onClick={recupId} id="5"className="semaineDate"><p className="headDate">mercredi</p><p className="date">25/12/2019</p></button>
                    <button onClick={recupId} id="6"className="semaineDate"><p className="headDate">jeudi</p><p className="date">26/12/2019</p></button>
                    <button onClick={recupId} id="7"className="semaineDate"><p className="headDate">vendredi</p><p className="date">27/12/2019</p></button>
                </div>

        </div>
    )
}

export default SelectDate
